package fr.mgargadennec.blossom.core.role.model;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Table;import org.hibernate.envers.Audited;import fr.mgargadennec.blossom.core.common.model.common.BlossomAbstractEntity;import fr.mgargadennec.blossom.core.common.model.common.BlossomResourceType;import fr.mgargadennec.blossom.core.role.constants.BlossomRoleConst;@Entity(name = "BoRole")@Table(name = "BLOSSOM_ROLE")@Audited@BlossomResourceType(BlossomRoleConst.BLOSSOM_ROLE_ENTITY_NAME)public class BlossomRolePO extends BlossomAbstractEntity {  @Column(name = "NAME", length = 30)  private String name = null;  @Column(name = "DESCRIPTION")  private String description;  public void setName(String name) {    this.name = name;  }  public String getName() {    return name;  }  public String getDescription() {    return description;  }  public void setDescription(String description) {    this.description = description;  }}